import React from "react";
import { useNavigate } from "react-router-dom";
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Box,
  IconButton,
} from "@mui/material";
import { DeleteForeverOutlined, ModeEditOutlineOutlined } from "@mui/icons-material";
import axios from "axios";

const Blog = ({ id, title, content, image, userName, isUser }) => {
  const navigate = useNavigate();

  const handleViewFullPost = () => {
    navigate(`/blogs/${id}`);
  };

  const handleEdit = (event) => {
    event.stopPropagation();
    navigate(`/myBlogs/${id}`);
  };

  const deleteRequest = async () => {
    try {
      await axios.delete(`http://localhost:3001/api/blog/${id}`);
      window.location.reload();
    } catch (err) {
      console.log(err);
    }
  };

  const handleDelete = (event) => {
    event.stopPropagation();
    deleteRequest();
  };

  return (
    <Box
      display="flex"
      justifyContent="center"
      alignItems="center"
      onClick={handleViewFullPost}
      sx={{ cursor: "pointer" }}
    >
      <Card
        sx={{
          width: "60%",  // Adjust the width as needed
          margin: "20px auto", // Center the card and add vertical spacing
          padding: 2,
          boxShadow: "5px 5px 10px #ccc",
          ":hover": { boxShadow: "10px 10px 20px #ccc" },
        }}
      >
        <CardMedia component="img" height="194" image={image} alt={title} />
        <CardContent>
          <Typography variant="h6">{title}</Typography>
          <Typography variant="body2" color="text.secondary">
            {content.substring(0, 100)}... {/* Show only first 100 characters */}
          </Typography>
          <Typography variant="body2" color="text.secondary">
            By {userName}
          </Typography>
          {isUser && (
            <Box display="flex" justifyContent="flex-end">
              <IconButton onClick={handleEdit}>
                <ModeEditOutlineOutlined color="info" />
              </IconButton>
              <IconButton onClick={handleDelete}>
                <DeleteForeverOutlined color="error" />
              </IconButton>
            </Box>
          )}
        </CardContent>
      </Card>
    </Box>
  );
};

export default Blog;
