import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";
import {
  Box,
  Typography,
  CircularProgress,
  Card,
  CardContent,
  CardMedia,
  Button,
  TextField,
  Avatar,
  IconButton,
} from "@mui/material";
import { DeleteForeverOutlined, ModeEditOutlineOutlined } from "@mui/icons-material";

function BlogDetail() {
  const [blog, setBlog] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const { id } = useParams();

  const fetchDetails = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/api/blog/${id}`);
      const data = res.data;
      setBlog(data.blog);
      setLoading(false);
    } catch (err) {
      setError("Error fetching blog details");
      setLoading(false);
    }
  };

  const fetchComments = async () => {
    try {
      const res = await axios.get(`http://localhost:3001/api/blog/${id}/comments`);
      const data = res.data;
      setComments(data.comments);
    } catch (err) {
      setError("Error fetching comments");
    }
  };

  const handleAddComment = async () => {
    try {
      const res = await axios.post(`http://localhost:3001/api/blog/${id}/comments`, {
        content: newComment,
        userId: "YOUR_USER_ID", // Replace with actual user ID
      });
      setComments((prevComments) => [...prevComments, res.data.comment]);
      setNewComment("");
    } catch (err) {
      setError("Error adding comment");
    }
  };

  const handleEdit = () => {
    navigate(`/myBlogs/${id}`);
  };

  const deleteRequest = async () => {
    try {
      await axios.delete(`http://localhost:3001/api/blog/${id}`);
      navigate("/");
    } catch (err) {
      console.log(err);
    }
  };

  const handleDelete = () => {
    deleteRequest().then(() => navigate("/"));
  };

  useEffect(() => {
    fetchDetails();
    fetchComments();
  }, [id]);

  if (loading) {
    return <CircularProgress />;
  }

  if (error) {
    return <Typography color="error">{error}</Typography>;
  }

  const isUser = localStorage.getItem("userId") === blog.user._id;

  return (
    <Box display="flex" flexDirection="column" alignItems="center">
      <Card sx={{ width: "60%", margin: 2 }}>
        <CardMedia component="img" height="400" image={blog.image} alt={blog.title} />
        <CardContent>
          <Typography variant="h4" component="div">
            {blog.title}
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            {blog.content}
          </Typography>
          <Typography variant="subtitle1" color="text.secondary">
            By {blog.user.name}
          </Typography>
          {isUser && (
            <Box display="flex" justifyContent="flex-end">
              <IconButton onClick={handleEdit}>
                <ModeEditOutlineOutlined color="info" />
              </IconButton>
              <IconButton onClick={handleDelete}>
                <DeleteForeverOutlined color="error" />
              </IconButton>
            </Box>
          )}
        </CardContent>
      </Card>

      <Box sx={{ width: "60%", margin: 2 }}>
        <Typography variant="h5">Comments</Typography>
        {comments.map((comment) => (
          <Box key={comment._id} sx={{ marginBottom: 2 }}>
            <Typography variant="body2">
              <strong>{comment.user.name}</strong>: {comment.content}
            </Typography>
          </Box>
        ))}
        <TextField
          fullWidth
          variant="outlined"
          label="Add a comment"
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          sx={{ marginBottom: 2 }}
        />
        <Button variant="contained" color="primary" onClick={handleAddComment}>
          Submit
        </Button>
      </Box>
    </Box>
  );
}

export default BlogDetail;
