import React from "react";
import { useNavigate } from "react-router-dom";
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Box,
  Button,
} from "@mui/material";

const Blog = ({ id, title, content, image, userName, handleEdit }) => {
  const navigate = useNavigate();

  const handleViewFullPost = () => {
    navigate(`/blogs/${id}`);
  };

  return (
    <Box
      display="flex"
      justifyContent="center"
      alignItems="center"
      sx={{ cursor: "pointer" }}
    >
      <Card
        sx={{
          width: "60%", // Adjust the width as needed
          margin: "20px auto", // Center the card and add vertical spacing
          padding: 2,
          boxShadow: "5px 5px 10px #ccc",
          ":hover": { boxShadow: "10px 10px 20px #ccc" },
        }}
      >
        <CardMedia component="img" height="194" image={image} alt={title} />
        <CardContent>
          <Typography variant="h6">{title}</Typography>
          <Typography variant="body2" color="text.secondary">
            {content.substring(0, 100)}... {/* Show only first 100 characters */}
          </Typography>
          <Button onClick={handleViewFullPost} sx={{ mr: 1 }}>
            View Post
          </Button>
          <Button onClick={() => handleEdit(id)} variant="outlined">
            Edit
          </Button>
        </CardContent>
      </Card>
    </Box>
  );
};

export default Blog;
