import React from "react";
import { useNavigate } from "react-router-dom";
import {
  Card,
  Avatar,
  CardContent,
  CardHeader,
  Typography,
  CardMedia,
  Box,
  IconButton,
} from "@mui/material";
import {
  DeleteForeverOutlined,
  ModeEditOutlineOutlined,
} from "@mui/icons-material";
import axios from "axios";

const Blog = ({ title, content, image, userName, isUser, id }) => {
  const navigate = useNavigate();

  const handleEdit = () => {
    navigate(`/myBlogs/${id}`);
  };

  const deleteRequest = async () => {
    try {
      const res = await axios.delete(`http://localhost:3001/api/blog/${id}`);
      return res.data;
    } catch (err) {
      console.log(err);
    }
  };

  const handleDelete = () => {
    deleteRequest().then(() => navigate("/"));
  };

  const handleCardClick = () => {
    navigate(`/blog/${id}`);
  };

  return (
    <Card
      sx={{
        width: "50%",
        margin: "auto",
        marginTop: 2,
        padding: 2,
        boxShadow: "5px 5px 10px #ccc",
        ":hover": { boxShadow: "10px 10px 20px #ccc" },
      }}
    >
      {isUser && (
        <Box display={"flex"}>
          <IconButton onClick={handleEdit} sx={{ marginLeft: "auto" }}>
            <ModeEditOutlineOutlined color="info" />
          </IconButton>
          <IconButton onClick={handleDelete}>
            <DeleteForeverOutlined color="error" />
          </IconButton>
        </Box>
      )}
      <CardHeader
        avatar={
          <Avatar sx={{ bgcolor: "red" }} aria-label="recipe">
            {userName && userName.charAt(0)}
          </Avatar>
        }
        title={title}
        onClick={handleCardClick}
        style={{ cursor: "pointer" }}
      />
      <CardMedia
        component="img"
        height="194"
        image={image}
        alt={title}
        onClick={handleCardClick}
        style={{ cursor: "pointer" }}
      />
      <CardContent onClick={handleCardClick} style={{ cursor: "pointer" }}>
        <Typography variant="body2" color="text.secondary">
          <b>{userName}</b>: {content.slice(0, 100)}... {/* Display a preview */}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default Blog;
