components/Comment.js
import React, { useState, useEffect } from "react";
import axios from "axios";
import { Box, Button, TextField, Typography } from "@mui/material";

const Comment = ({ blogId }) => {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");

  const fetchComments = async () => {
    try {
      const response = await axios.get(`http://localhost:3001/api/blog/${blogId}/comments`);
      setComments(response.data.comments);
    } catch (error) {
      console.log("Error fetching comments:", error);
    }
  };

  const handleAddComment = async () => {
    try {
      const user = localStorage.getItem("userId");
      const response = await axios.post(`http://localhost:3001/api/blog/${blogId}/comments`, {
        content: newComment,
        userId: user, // Change "user" to "userId"
      });
      setNewComment("");
      fetchComments();
    } catch (error) {
      console.log("Error adding comment:", error);
    }
  };

  useEffect(() => {
    fetchComments();
  }, [blogId]);

  return (
    <Box>
      <Typography variant="h6">Comments</Typography>
      <Box>
        {comments.map((comment) => (
          <Typography key={comment._id}>
            {comment.content} - {comment.user.name}
          </Typography>
        ))}
      </Box>
      <TextField
        label="Add a comment"
        value={newComment}
        onChange={(e) => setNewComment(e.target.value)}
        fullWidth
        multiline
        rows={4}
        variant="outlined"
        margin="normal"
      />
      <Button variant="contained" color="primary" onClick={handleAddComment}>
        Add Comment
      </Button>
    </Box>
  );
};

export default Comment;


