// components/UserBlogs.js
import React, { useState, useEffect } from "react";
import axios from "axios";

const UserBlogs = ({ userId }) => {
  const [blogs, setBlogs] = useState([]);
  const [error, setError] = useState(null);

  const sendRequest = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/api/user/${userId}/blogs`);
      setBlogs(response.data.user.blogs); // Adjust according to your API response structure
    } catch (err) {
      setError(err);
      console.error("Error fetching user blogs:", err);
    }
  };

  useEffect(() => {
    sendRequest();
  }, [userId]);

  if (error) {
    return <div>Error: {error.message}</div>;
  }

  return (
    <div>
      {blogs.map((blog) => (
        <div key={blog._id}>
          <h2>{blog.title}</h2>
          <p>{blog.content}</p>
          {/* Display other blog details */}
        </div>
      ))}
    </div>
  );
};

export default UserBlogs;
